

    class A {
    	public void  A() {
    		System.out.println("A");
    		
    	}
    }
    class B extends A{
    	public void display() {
    	System.out.println("B");
    }
    	public class Inheritance {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
	B b=new B();
   b.display();
	
		
   
	}}}
    

